package Lec1;

public class Var_Add {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 43;
		int b = 73;
		int c = a + b;
		System.out.println(c);
		c = a - b;// update kiya hu c ko
		System.out.println(c);
		int c1 = a - b;
		System.out.println(c1);
		System.out.println(a * b);
		System.out.println(a / b);
		System.out.println(a % b);
	}

}
